
jQuery(document).ready(function(a) {

    if(jQuery("#cpn_auto_import").val()=='Disabled')
    {
      jQuery(".cpn_import_section").hide();
    }
    else
    {
      jQuery(".cpn_import_section").show();
    }
    if(jQuery("#cpn_auto_export").val()=='Disabled')
    {
      jQuery(".cpn_export_section").hide();
    }
    else
    {
      jQuery(".cpn_export_section").show();
    }
    jQuery( "#datepicker1" ).datepicker({
        dateFormat: "yy-mm-dd",
        changeMonth: true,//this option for allowing user to select month
        changeYear: true, //this option for allowing user to select from year range
    });
    jQuery( "#datepicker2" ).datepicker({
        dateFormat: "yy-mm-dd",
        changeMonth: true,//this option for allowing user to select month
        changeYear: true, //this option for allowing user to select from year range
    });
    "use strict";
    a("select[name=cpn_auto_export]").change(function() {
        if("Disabled" === a(this).val()){
            a(".cpn_export_section").hide();
        }else{
            a(".cpn_export_section").show();
        }
    })   
    if(woocommerce_coupon_csv_cron_params.cpn_auto_export === 'Disabled'){
        a(".cpn_export_section").hide();
    };
    a("select[name=cpn_auto_import]").change(function() {
        if("Disabled" === a(this).val()){
            a(".cpn_import_section").hide();
        }else{
            a(".cpn_import_section").show();
        }
    })
    if(woocommerce_coupon_csv_cron_params.cpn_auto_import === 'Disabled'){
        a(".cpn_import_section").hide();
    }
    // Listen for click on toggle checkbox
        a('#selectall').click(function(event) {   
                // Iterate each checkbox
               a(':checkbox').each(function() {
                    this.checked = true;
                });
        });
        a('#unselectall').click(function(event) {   
                // Iterate each checkbox
               a(':checkbox').each(function() {
                    this.checked = false;
                });
        });

});

